import React, { useState, Fragment } from 'react'
import AddProductForm from './forms/AddProductForm'
import EditProductForm from './forms/EditProductForm'
import UserTable from './tables/UserTable'

const App = () => {
	// Data
	const ProductsData = [
		{ id: 1, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 2, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 3, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 4, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 5, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 6, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
	]

	const initialFormState = { id: null, descripcion: '', marca: '', valor:"", estado:"" }

	// Setting state
	const [ products, setProducts ] = useState(ProductsData)
	const [ currentProduct, setCurrentProduct ] = useState(initialFormState)
	const [ editing, setEditing ] = useState(false)

	// CRUD operations
	const addProduct = product => {
		product.id = products.length + 1
		setProducts([ ...products, product ])
	}

	const deleteProduct = id => {
		setEditing(false)

		setProducts(products.filter(product => product.id !== id))
	}

	const updateProduct = (id, updatedProduct) => {
		setEditing(false)

		setProducts(products.map(product => (product.id === id ? updatedProduct : product)))
	}

	const editRow = product => {
		setEditing(true)

		setCurrentProduct({ id: product.id, descripcion: product.descripcion, marca: product.marca, valor: product.valor, estado: product.estado })
	}

	return (
		<div className="container">
			<h1>CRUD App with Hooks</h1>
			<div className="flex-row">
				<div className="flex-large">
					{editing ? (
						<Fragment>
							<h2> Editar Producto</h2>
							<EditProductForm
								editing={editing}
								setEditing={setEditing}
								currentProduct={currentProduct}
								updateProduct={updateProduct}
							/>
						</Fragment>
					) : (
						<Fragment>
							<h2> Añadir producto </h2>
							<AddProductForm addProduct={addProduct} />
						</Fragment>
					)}
				</div>
				<div className="flex-large">
					<h2>Ver productos</h2>
					<UserTable products={products} editRow={editRow} deleteProduct={deleteProduct} />
				</div>
			</div>
		</div>
	)
}

export default App;
