import React, { useState } from 'react'

const AddProductForm = props => {
	const initialFormState = { id: null, descripcion: '', marca:"", valor:"", estado:"" }
	const [ product, setProduct ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setProduct({ ...product, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
				if (!product.id || !product.descripcion || !product.marca || !product.valor || !product.estado) return

				props.addProduct(product)
				setProduct(initialFormState)
			}}
		>
			<label> Id </label>
			<input type="text" name="id" value={product.id} onChange={handleInputChange} />
			<label> Descripci√≥n </label>
			<input type="text" name="descripcion" value={product.descripcion} onChange={handleInputChange} />
			<label> Marca </label>
			<input type="text" name="marca" value={product.marca} onChange={handleInputChange} />
			<label> Valor Unit. </label>
			<input type="text" name="valor" value={product.valor} onChange={handleInputChange} />
			<label> Estado </label>
			<input type="text" name="estado" value={product.estado} onChange={handleInputChange} />
			<button> Agregar </button>
		</form>
	)
}

export default AddProductForm;
