import React, { useState, Fragment } from 'react'
import AddUserForm from 'forms/AddProductForm'
import EditUserForm from './forms/EditUserForm'
import UserTable from './tables/UserTable'

const App = () => {
	// Data
	const usersData = [
		{ id: 1, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 2, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 3, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 4, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 5, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
		{ id: 6, descripcion: "adadadadada", marca: "dsdsd", valor: "adadad", estado: "adaadada" },
	]

	const initialFormState = { id: null, descripcion: '', marca: '', valor:"", estado:"" }

	// Setting state
	const [ products, setProducts ] = useState(usersData)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)
	const [ editing, setEditing ] = useState(false)

	// CRUD operations
	const addUser = user => {
		user.id = products.length + 1
		setProducts([ ...products, user ])
	}

	const deleteUser = id => {
		setEditing(false)

		setProducts(products.filter(user => user.id !== id))
	}

	const updateUser = (id, updatedUser) => {
		setEditing(false)

		setProducts(products.map(user => (user.id === id ? updatedUser : user)))
	}

	const editRow = user => {
		setEditing(true)

		setCurrentUser({ id: user.id, name: user.name, username: user.username })
	}

	return (
		<div className="container">
			<h1>CRUD App with Hooks</h1>
			<div className="flex-row">
				<div className="flex-large">
					{editing ? (
						<Fragment>
							<h2>Edit user</h2>
							<EditUserForm
								editing={editing}
								setEditing={setEditing}
								currentUser={currentUser}
								updateUser={updateUser}
							/>
						</Fragment>
					) : (
						<Fragment>
							<h2> AÃ±adir producto </h2>
							<AddUserForm addUser={addUser} />
						</Fragment>
					)}
				</div>
				<div className="flex-large">
					<h2>Ver productos</h2>
					<UserTable users={products} editRow={editRow} deleteUser={deleteUser} />
				</div>
			</div>
		</div>
	)
}

export default App;
